import React, { useEffect, useState } from 'react';
import './style.css';

//apiUrl = http://localhost:8080/api/users



function App() {

  const[users, setUsers] = useState([]);

  useEffect(() => {

    function loadApi() {
      let url = 'http://localhost:8080/api/users';
      fetch(url).then((result) => {
        result.json().then((resultJson) => {
          setUsers(resultJson);
        });
      });  
    }

    loadApi();

  }, []);

  function getUsers() {
    console.clear();

    users.forEach((user) => {
      console.log(user);
    })
  }

  return (
    <div className="container">
      <header>
        <strong>4Chat</strong>
      </header>
      <button className="btn btn-outline-primary" onClick={getUsers}>Get all Users</button>
    </div>
  );
}

export default App;
